mapper.select:
select id , -- id 自增 question_name questionName, -- '简介' description , -- '描述' type , -- bug,需求,优化,运维 page , -- '所属页面' module, -- '所属模块' tester_reply testerReply,-- '测试回复', is_solve isSolve,-- '是否解决', develop , on_duty tester, DATE_FORMAT(question_date,'%Y-%m-%d') questionDate from dafacloud_online_question
AND is_solve = #{isSolve}
AND type = #{type}
AND on_duty like CONCAT(CONCAT('%',#{tester}),'%')
AND question_name like CONCAT(CONCAT('%',#{questionName}),'%')
AND DATE_FORMAT(question_date,'%Y-%m-%d') between #{startQuestionDate} and #{endQuestionDate}
order by question_date desc,insertTime desc limit #{pageNum},#{pageSize}

select count(1) from dafacloud_online_question


        AND is_solve = #{isSolve}


        AND type = #{type}


        AND on_duty like CONCAT(CONCAT('%',#{tester}),'%')


        AND question_name like CONCAT(CONCAT('%',#{questionName}),'%')



        AND DATE_FORMAT(question_date,'%Y-%m-%d') between #{startQuestionDate} and #{endQuestionDate}

select (@i:=@i+1) num, a.id , -- id 自增 a.question_name questionName, -- '简介' a.description , -- '描述' a.type , -- bug,需求,优化,运维 -- page , -- '所属页面' -- module, -- '所属模块' a.tester_reply testerReply,-- '测试回复', a.is_solve isSolve,-- '是否解决', a.develop , a.on_duty tester, DATE_FORMAT(question_date,'%Y-%m-%d') questionDate from dafacloud_online_question a,(select @i:=0) t
AND a.s_solve = #{isSolve}
AND a.type = #{type}
AND a.on_duty like CONCAT(CONCAT('%',#{tester}),'%')
AND a.question_name like CONCAT(CONCAT('%',#{questionName}),'%')
AND DATE_FORMAT(a.question_date,'%Y-%m-%d') between #{startQuestionDate} and #{endQuestionDate}
order by a.question_date desc,a.insertTime desc

mapper.insert:
        insert into dafacloud_online_question(
		-- id , -- id 自增
        question_name , -- '简介'
        description , -- '描述'
        type , -- bug,需求,优化,运维
        page , -- '所属页面'
        module, -- '所属模块'
        tester_reply ,-- '测试回复',
        is_solve ,-- '是否解决',
        develop ,-- '开发人员',
        on_duty, -- '值班人员',
        question_date
		)values (
			#{questionName}
            ,#{description}
            ,#{type}
            ,#{page}
            ,#{module}
            ,#{testerReply}
            ,#{isSolve}
            ,#{develop}
            ,#{tester}
            ,#{questionDate}
		)
